import './App.css';
import React from 'react';
import Banner from './components/Banner';
import banner1 from "./assets/images/banner-1.png"
import banner2 from "./assets/images/banner-2.png"
import Header from './components/Header';
import FlatList from './components/FlatList';
import Dropdown from './components/Dropdown';
import Footer from './components/Footer';
import Tag from './components/Tag';
import { useEffect, useState } from "react";
import axios from "axios"
import flats from './datas/data';

import { BrowserRouter as Router, Route, Switch } from 'react-router-dom/cjs/react-router-dom.min';
import MainPage from './pages/Main';
import AboutPage from './pages/About';
import Error from './pages/Error';
import FlatPage from './pages/Flat';


class App extends React.Component {

  constructor() {
    super();
    this.state = {flats: []};
  }

  async getData() {
    const res = await axios('https://raw.githubusercontent.com/iliasse-e/iliasse-e-IliasseELABOUYI_11_31122021/main/data.json');
    return await res.json();
  }

  componentDidMount() {
    if (!this.state.data) {
      this.getData().then(data => this.setState({flats}))
      .catch(err => {"error"});
    }
    console.log(this.state)
  }

  render() {

    return <div className="App">
        <Router>
          <Header />
          <Switch>
              <Route exact path="/"> 
                <MainPage flats={this.state.flats}/>
              </Route>

              <Route path="/About"> 
                <AboutPage />
              </Route>

              <Route path="/flat/:id"> 
                <FlatPage flats={this.state.flats}/>
              </Route>

              <Route path="/"> 
                <Error />
              </Route>

          </Switch>
          <Footer />
        </Router>
    </div>
  };
}

export default App;
