import React from "react";
import chevronDown from "../assets/images/Vector-down.svg"
import "../styles/Dropdown.css"
import flats from "../datas/data";
import EquipmentList from "./Flat-equipement-list";
import FlatDescription from "./Flat-description";

class Dropdown extends React.Component {

    state = {
        show: false
    }

    toggleDropdown() {
        this.setState({
            show: !this.state.show
        })

    }

    render() {
        return <div id={this.props.title}>
            <div className="dropdown" data-hidden="true"  onClick={this.setState({
            show: !this.state.show
        })}>
                <span className="dropdown__title">{this.props.title}</span>
                <img className="dropdown__chevron-down" src={chevronDown} alt="dÃ©rouler les informations" />
            </div>
            {this.props.description && this.state.show ? <FlatDescription description={this.props.text}/>: null}
            {this.props.equipment && <EquipmentList flatNumber={0}/>}
        </div>

    }
}

export default Dropdown
