import './App.css';
import React from 'react';
import Banner from './components/Banner';
import banner1 from "./assets/images/banner-1.png"
import banner2 from "./assets/images/banner-2.png"
import Header from './components/Header';
import FlatList from './components/FlatList';
import Dropdown from './components/Dropdown';
import Footer from './components/Footer';
import { useEffect, useState } from "react";
import axios from "axios"

import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import MainPage from './pages/Main';
import AboutPage from './pages/About';
import Error from './pages/Error';
import FlatPage from './pages/Flat';
import { ABOUT, ERROR, FLAT, HOME } from './Routes';
import Loader from './components/Loader';
import { getFlats } from './datas/getFlats';


class App extends React.Component {

  constructor() {
    super();
    this.state = {
      flats: getFlats,
      loading: true
    };
  }

  async getData() {
    return await axios('https://raw.githubusercontent.com/iliasse-e/iliasse-e-IliasseELABOUYI_11_31122021/main/data.json');
  }

  componentDidMount() {

    this.getData().then(data => {
      this.setState({flats: data.data})
      console.log(this.state.flats)
      getFlats = this.state.flats
      this.setState({loading: false})
    })
    .catch(err => {console.log(err)});

  }

  render() {
    const {flatsData} = this.state.flats;

    return <div className="App">
        <Router>
          {this.state.loading} ? (
            <Loader/>
          ) : 
          (<Header />
          <Switch>
              <Route exact path={HOME}> 
                <MainPage flats={flatsData}/>
              </Route>

              <Route exact path={ABOUT}> 
                <AboutPage />
              </Route>

              <Route exact strict path={FLAT}> 
                <FlatPage flats={flatsData}/>
              </Route>

              <Route component={Error}/> 

          </Switch>
          <Footer />)
        </Router>
    </div>
  };
}

export default App;
