import React from "react";
import "../styles/Gallery.css"
import arrowPrevious from "../assets/images/arrow-left.svg"

class Gallery extends React.Component {

    render() {
        const pictures = this.props.pictures
        return <div className="flat-page__gallery" id="gallery">
            <button className="flat-page__gallery__prev" aria-label="image précedente" id="gallery-prev"></button>
            <img className="dropdown__chevron-left" src={arrowPrevious} alt="dérouler les informations" />

            {pictures.map((picture) =>
                <div className="image-container">
                    <img className="flat-page__gallery__img" key={picture.value} src={picture} alt="photo du bien immobilier"/>
                </div>
            )}

            <button class="flat-page__gallery__next" aria-label="image suivante" id="gallery-next"></button>
        </div>

    }
}

export default Gallery
