import './App.css';
import React from 'react';
import Banner from './components/Banner';
import banner1 from "./assets/images/banner-1.png"
import banner2 from "./assets/images/banner-2.png"
import Header from './components/Header';
import FlatList from './components/FlatList';
import Dropdown from './components/Dropdown';
import Footer from './components/Footer';
import Tag from './components/Tag';
import { useEffect, useState } from "react";
import axios from "axios"
import flats from './datas/data';

import { BrowserRouter as Router, Route, Switch } from 'react-router-dom/cjs/react-router-dom.min';
import MainPage from './pages/Main';
import AboutPage from './pages/About';


class App extends React.Component {

  constructor() {
    super();
    this.state = {flats: flats};
  }

  async getData() {
    const res = await axios('https://raw.githubusercontent.com/iliasse-e/iliasse-e-IliasseELABOUYI_11_31122021/main/data.json');
    return await res.json();
  }

  componentDidMount() {
    if (!this.state.data) {
      this.getData().then(data => this.setState({flats}))
      .catch(err => {"error"});
    }
    console.log(this.state)
  }

  render() {

    return <div className="App">
        <Router>
          <Header />
              <Route exact path="/"> 
                <MainPage flats={this.state.flats}/>
              </Route>
              <Route exact path="/About"> 
                <AboutPage />
              </Route>

          <div>
          {this.state.flats ? <h1>Loading...</h1> : <h1>{this.state.flats}</h1>}
          </div>
          <Footer />
        </Router>
    </div>
  };
}

export default App;
